INSERT INTO `wp_posts` VALUES("127", "1", "2016-05-16 20:19:35", "2016-05-16 20:19:35", "<p style=\"text-align: justify;\">Welcome and thank you for visiting our official website. <em>Tianis</em> provides a selection of luxury furnished and serviced apartments accommodation for short and extended stays in <em>Kotor</em>. Families, corporate business travelers and vacationers will all find themselves equally at home in our apartments.</p>\r\n<p class=\"less\" style=\"text-align: justify;\">Our apartments offer all you need for a successful stay in Kotor, whether it is for business or pleasure and we strive to offer you the best personal service possible.</p>\r\n\r\n\r\n[caption id=\"attachment_12\" align=\"alignright\" width=\"150\"]<a href=\"http://localhost:8000/wp-content/uploads/2016/05/Tianis.jpg\"><img class=\"size-thumbnail wp-image-12\" src=\"http://localhost:8000/wp-content/uploads/2016/05/Tianis-150x150.jpg\" alt=\"Tianis Apartments\" width=\"150\" height=\"150\" /></a> Tianis Apartments[/caption]\r\n<p class=\"red\" style=\"text-align: justify; font-size: 18px; font-weight: bold;\">About us</p>\r\n\r\n<div class=\"block browsers\">\r\n<p style=\"text-align: justify;\">We offer our clients a top-quality service that includes not just reservation of accommodation, but care about the guests from their arrival to departure. We provide guests with all available information and help them to spend their vacation as pleasantly as possible.</p>\r\n\r\n</div>\r\n&nbsp;\r\n\r\n<hr style=\"color: lightgray;\" />\r\n\r\n[caption id=\"attachment_24\" align=\"alignleft\" width=\"150\"]<a href=\"http://localhost:8000/wp-content/uploads/2016/05/yaris.jpg\"><img class=\"wp-image-24 size-thumbnail\" src=\"http://localhost:8000/wp-content/uploads/2016/05/yaris-150x150.jpg\" alt=\"Toyota Yaris\" width=\"150\" height=\"150\" /></a> Toyota Yaris[/caption]\r\n<p class=\"blue\" style=\"font-size: 18px; font-weight: bold;\"><a href=\"/rental-services\">Rental services</a></p>\r\n\r\n<div class=\"block wide\">\r\n<p style=\"text-align: justify;\">Since most travelers prefer to be in charge of their own time and movement while touring the Boka Bay instead of relying on public transportation, we provide several options for you: rental cars, scooters, bicycles and boat.</p>\r\n\r\n</div>", "Home", "", "inherit", "closed", "closed", "", "5-revision-v1", "", "", "2016-05-16 20:19:35", "2016-05-16 20:19:35", "", "5", "http://localhost:8000/5-revision-v1", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("128", "1", "2016-05-16 20:20:30", "2016-05-16 20:20:30", "<p style=\"text-align: justify;\">Welcome and thank you for visiting our official website. <em>Tianis</em> provides a selection of luxury furnished and serviced apartments accommodation for short and extended stays in <em>Kotor</em>. Families, corporate business travelers and vacationers will all find themselves equally at home in our apartments.</p>\r\n<p class=\"less\" style=\"text-align: justify;\">Our apartments offer all you need for a successful stay in Kotor, whether it is for business or pleasure and we strive to offer you the best personal service possible.</p>\r\n\r\n\r\n[caption id=\"attachment_12\" align=\"alignright\" width=\"150\"]<a href=\"http://localhost:8000/wp-content/uploads/2016/05/Tianis.jpg\"><img class=\"size-thumbnail wp-image-12\" src=\"http://localhost:8000/wp-content/uploads/2016/05/Tianis-150x150.jpg\" alt=\"Tianis Apartments\" width=\"150\" height=\"150\" /></a> Tianis Apartments[/caption]\r\n<p class=\"red\" style=\"text-align: justify; font-size: 18px; font-weight: bold;\"><a href=\"/accommodation/apartments\">About us</a></p>\r\n\r\n<div class=\"block browsers\">\r\n<p style=\"text-align: justify;\">We offer our clients a top-quality service that includes not just reservation of accommodation, but care about the guests from their arrival to departure. We provide guests with all available information and help them to spend their vacation as pleasantly as possible.</p>\r\n\r\n</div>\r\n&nbsp;\r\n\r\n<hr style=\"color: lightgray;\" />\r\n\r\n[caption id=\"attachment_24\" align=\"alignleft\" width=\"150\"]<a href=\"http://localhost:8000/wp-content/uploads/2016/05/yaris.jpg\"><img class=\"wp-image-24 size-thumbnail\" src=\"http://localhost:8000/wp-content/uploads/2016/05/yaris-150x150.jpg\" alt=\"Toyota Yaris\" width=\"150\" height=\"150\" /></a> Toyota Yaris[/caption]\r\n<p class=\"blue\" style=\"font-size: 18px; font-weight: bold;\"><a href=\"/renta